	(25, 'PERANCANGAN APLIKASI/SOFTWARE DOCUMENT MANAGEMENT SYSTEM (DMS)', 'SOON', 'for soon.jpg', '<div>SOON</div>', '<div>SOON</div>', NULL, '<div>SOON</div>', '2023-07-08 00:42:56', '2023-07-08 00:42:56'),
