INSERT INTO `news` (`id`, `title`, `image`, `body`, `created_at`, `updated_at`) VALUES
	(11, 'Kegiatan Bimbingan Teknis Peningkatan Kualitas Komponen Industri Teknologi Informasi dan Komunikasi di PT. Wellbest', 'PT Wellbest.jpg', '<div>kegiatan Bimbingan Teknis Peningkatan Kualitas Komponen Industri Teknologi dan Komunikasi (Laptop) dimaksudkan untuk memfasilitasi perusahaan industri Teknologi dan Komunikasi (Laptop), di bawah binaan Direktorat Industri Elektronika dan Telematika, Ditjen ILMATE agar mampu melakukan pengelolaan quality assurance sesuai dengan SKKNI 199/2021.<br><br>Sedangkan tujuannya untuk meningkatkan kemampuan sumber daya manusia perusahaan di bidang quality assurance. Keluaran yang diinginkan dari Bimbingan Teknis Peningkatan Kualitas Komponen Industri Teknologi dan Komunikasi (Laptop) adalah terfasilitasinya 3 (tiga) industri Teknologi dan Komunikasi (Laptop), di bawah binaan Direktorat Industri Elektronika dan Telematika, Ditjen ILMATE dalam rangka peningkatan kompetensi sumber daya manusia di bidang quality assurance.</div>', '2023-07-06 06:56:35', '2023-07-06 07:38:34'),
	(12, 'Kegiatan Bimbingan Teknis Peningkatan Kualitas Komponen Industri Teknologi Informasi dan Komunikasi (HKT) di PT. Totan Global Indonesia.', 'PT.Totan.jpg', '<div>kegiatan Bimbingan Teknis Peningkatan Kualitas Komponen Industri Teknologi dan Komunikasi (HKT) dimaksudkan untuk memfasilitasi perusahaan industri Teknologi dan Komunikasi (HKT), di bawah binaan Direktorat Industri Elektronika dan Telematika, Ditjen ILMATE agar mampu melakukan pengelolaan quality assurance sesuai dengan SKKNI 199/2021.<br><br>Sedangkan tujuannya untuk meningkatkan kemampuan sumber daya manusia perusahaan di bidang quality assurance.</div>', '2023-07-06 07:14:31', '2023-07-06 07:38:23'),
	(13, 'Kegiatan Bimbingan Teknis Peningkatan Kualitas Komponen Industri Teknologi Informasi dan Komunikasi (HKT) di PT. Xiaolin Electronics Indonesia.', 'PT.Xiaolin.jpg', '<div>kegiatan Bimbingan Teknis Peningkatan Kualitas Komponen Industri Teknologi dan Komunikasi (HKT) dimaksudkan untuk memfasilitasi perusahaan industri Teknologi dan Komunikasi (HKT), di bawah binaan Direktorat Industri Elektronika dan Telematika, Ditjen ILMATE agar mampu melakukan pengelolaan quality assurance sesuai dengan SKKNI 199/2021.<br><br>Sedangkan tujuannya untuk meningkatkan kemampuan sumber daya manusia perusahaan di bidang quality assurance.</div>', '2023-07-06 07:17:39', '2023-07-06 07:38:16'),
	(14, 'Kegiatan Bimbingan Teknis Peningkatan Mutu Produsen Industri Elektronika di PT. Hisense International Indonesia', 'PT. GAS.jpg', '<div>Kegiatan Bimbingan Teknis Peningkatan Mutu Produsen Industri Elektronika dimaksudkan untuk memfasilitasi perusahaan industri Elektronika, di bawah binaan Direktorat Industri Elektronika dan Telematika, Ditjen ILMATE agar mampu melakukan pengelolaan mutu sesuai Quality Assurance berdasarkan SKKNI 199/2021.<br><br>Sedangkan tujuannya untuk meningkatkan kemampuan sumber daya manusia perusahaan di bidang Quality Assurance.</div>', '2023-07-06 07:37:27', '2023-07-06 07:38:09'),
	(15, 'Kegiatan Bimbingan Teknis Peningkatan Mutu Produsen Industri Elektronika di PT. Sanken Argadwija', 'PT. Sanken.jpg', '<div>Kegiatan Bimbingan Teknis Peningkatan Mutu Produsen Industri Elektronika dimaksudkan untuk memfasilitasi perusahaan industri Elektronika, di bawah binaan Direktorat Industri Elektronika dan Telematika, Ditjen ILMATE agar mampu melakukan pengelolaan mutu sesuai Quality Assurance berdasarkan SKKNI 199/2021.<br><br>Sedangkan tujuannya untuk meningkatkan kemampuan sumber daya manusia perusahaan di bidang Quality Assurance.</div>', '2023-07-06 07:39:35', '2023-07-06 07:39:35'),
	(16, 'Kegiatan Bimbingan Teknis Peningkatan Mutu Produsen Industri Elektronika di PT. Selaras Citra Nusantara Perkasa Tbk', 'PT Selaras.jpg', '<div>Kegiatan Bimbingan Teknis Peningkatan Mutu Produsen Industri Elektronika dimaksudkan untuk memfasilitasi perusahaan industri Elektronika, di bawah binaan Direktorat Industri Elektronika dan Telematika, Ditjen ILMATE agar mampu melakukan pengelolaan mutu sesuai Quality Assurance berdasarkan SKKNI 199/2021.<br><br>Sedangkan tujuannya untuk meningkatkan kemampuan sumber daya manusia perusahaan di bidang Quality Assurance.</div>', '2023-07-06 07:40:21', '2023-07-06 07:40:21'),
	(17, 'Kegiatan Bimbingan Teknis Peningkatan Mutu Produsen Industri Elektronika di PT. Jinheung Electric Indonesia', 'PT. Jinheung.jpg', '<div>Kegiatan Bimbingan Teknis Peningkatan Mutu Produsen Industri Elektronika dimaksudkan untuk memfasilitasi perusahaan industri Elektronika, di bawah binaan Direktorat Industri Elektronika dan Telematika, Ditjen ILMATE agar mampu melakukan pengelolaan mutu sesuai Quality Assurance berdasarkan SKKNI 199/2021.<br><br>Sedangkan tujuannya untuk meningkatkan kemampuan sumber daya manusia perusahaan di bidang Quality Assurance.</div>', '2023-07-06 07:41:19', '2023-07-06 07:41:19'),
	(18, 'Kegiatan Bimbingan Teknis Peningkatan Mutu Produsen Industri Komponen Elektronika di PT. International Chemical Industry', 'PT. International Chemical Industry (1).jpg', '<div>Kegiatan Bimbingan Teknis Peningkatan Mutu Produsen Industri Komponen Elektronika dimaksudkan untuk memberikan fasilitasi kepada perusahaan industri Komponen Elektronika yang ada dibawah binaan Direktorat Industri Elektronika dan Telematika, Ditjen ILMATE agar mampu melakukan pengelolaan <em>quality assurance</em> sesuai dengan SKKNI (Standar Kompetensi Kerja Nasional Indonesia ) No. 199/2021.<br><br>Tujuan dari kegiatan ini adalah untuk meningkatkan kemampuan atau kompetensi sumber daya manusia perusahaan di bidang <em>quality assurance</em> agar dapat bersaing di era global. Keluaran yang diharapkan dari Bimbingan Teknis Peningkatan Mutu Produsen Industri Komponen Elektronika adalah terfasilitasinya 4 (empat) industri Komponen Elektronika, di bawah binaan Direktorat Industri Elektronika dan Telematika, Ditjen ILMATE dalam rangka peningkatan kompetensi sumber daya manusia dibidang <em>quality assurance</em>.&nbsp;</div>', '2023-07-06 07:49:23', '2023-07-06 07:49:23'),
	(19, 'Kegiatan Bimbingan Teknis Peningkatan Mutu Produsen Industri Komponen Elektronika di PT. Sanwa Parts Indonesia', 'PT. Sanwa Parts Indonesia (2).jpg', '<div>Kegiatan Bimbingan Teknis Peningkatan Mutu Produsen Industri Komponen Elektronika dimaksudkan untuk memberikan fasilitasi kepada perusahaan industri Komponen Elektronika yang ada dibawah binaan Direktorat Industri Elektronika dan Telematika, Ditjen ILMATE agar mampu melakukan pengelolaan <em>quality assurance</em> sesuai dengan SKKNI (Standar Kompetensi Kerja Nasional Indonesia ) No. 199/2021.<br><br>Tujuan dari kegiatan ini adalah untuk meningkatkan kemampuan atau kompetensi sumber daya manusia perusahaan di bidang <em>quality assurance</em> agar dapat bersaing di era global. Keluaran yang diharapkan dari Bimbingan Teknis Peningkatan Mutu Produsen Industri Komponen Elektronika adalah terfasilitasinya 4 (empat) industri Komponen Elektronika, di bawah binaan Direktorat Industri Elektronika dan Telematika, Ditjen ILMATE dalam rangka peningkatan kompetensi sumber daya manusia dibidang <em>quality assurance</em>.&nbsp;</div>', '2023-07-06 07:50:29', '2023-07-06 07:50:29'),
	(20, 'Kegiatan Bimbingan Teknis Peningkatan Mutu Produsen Industri Komponen Elektronika di PT. Haengsung Raya Indonesia', 'PT. Haeng Sung Raya Indonesia(3).jpg', '<div>Kegiatan Bimbingan Teknis Peningkatan Mutu Produsen Industri Komponen Elektronika dimaksudkan untuk memberikan fasilitasi kepada perusahaan industri Komponen Elektronika yang ada dibawah binaan Direktorat Industri Elektronika dan Telematika, Ditjen ILMATE agar mampu melakukan pengelolaan <em>quality assurance</em> sesuai dengan SKKNI (Standar Kompetensi Kerja Nasional Indonesia ) No. 199/2021.<br><br>Tujuan dari kegiatan ini adalah untuk meningkatkan kemampuan atau kompetensi sumber daya manusia perusahaan di bidang <em>quality assurance</em> agar dapat bersaing di era global. Keluaran yang diharapkan dari Bimbingan Teknis Peningkatan Mutu Produsen Industri Komponen Elektronika adalah terfasilitasinya 4 (empat) industri Komponen Elektronika, di bawah binaan Direktorat Industri Elektronika dan Telematika, Ditjen ILMATE dalam rangka peningkatan kompetensi sumber daya manusia dibidang <em>quality assurance</em>.&nbsp;</div>', '2023-07-06 07:51:29', '2023-07-06 07:51:29'),
	(21, 'Kegiatan Jasa Bimbingan dan Konsultasi Pendampingan Audit Surveillance ISO 9001:2015 Pada Pusat Pengembangan Sumber Daya Manusia Aparatur KESDM', 'Pendampingan PPSDMA.jpg', '<div>kegiatan Jasa Bimbingan dan Konsultasi Pendampingan Audit Surveillance ISO 9001:2015 adalah untuk memberikan pendampingan dalam kegiatan persiapan, pelaksanaan dan tindak lanjut kegiatan <em>surveillance</em> audit kedua Sistem Manajemen Mutu ISO 9001:2015 agar sertifikat sistem manajemen mutu ISO 9001:2015 dapat dipertahankan. Sedangkan tujuannya agar seluruh keperluan dokumen sistem manajemen terkait audit surveillance kedua dapat dipersiapkan dan sesuai dengan apa yang dipersyaratkan dalam standar, sehingga sistem dapat berjalan secara efektif dan sertifikat Sistem Manajemen Mutu yang telah diraih dapat dipertahankan.<br><br></div><div>Sasaran dari kegiatan Jasa Bimbingan dan Konsultasi Pendampingan Audit Surveillance ISO 9001:2015 Pada Pusat PengembanganSumber Daya Manusia Aparatur KESDM adalah agar dapat dipertahankannya Sertifikat Sistem Manajemen Mutu ISO 9001:2015 dari Lembaga Sertifikasi Sistem Mutu.&nbsp;</div>', '2023-07-06 07:57:51', '2023-07-06 07:57:51'),
	(22, 'Kegiatan Jasa Bimbingan dan Konsultansi Pemeliharaan Sistem Manajemen Mutu ISO 9001:2015 di PPSDM Aparatur KESDM', 'Pemeliharaan PPSDMA.jpg', '<div>Kegiatan ini adalah untuk memberikan pemahaman lebih mendalam mengenai implementasi dan teknik-teknik evaluasi yang sesuai dengan persyaratan Sistem Manajemen Mutu ISO 9001:2015 di Pusat Pengembangan Sumber Daya Manusia Aparatur KESDM, Sedangkan tujuannya adalah agar Pusat Pengembangan Sumber Daya Manusia Aparatur dapat menerapkan dan&nbsp; memelihara Sistem Manajemen Mutu ISO 9001:2015 secara konsisten sebagaimana yang telah dipersyaratkan dalam standar, sehingga sistem dapat berjalan secara efektif.<br><br></div><div>Sasaran dari kegiatan Jasa Bimbingan dan Konsultansi Pemeliharaan Sistem Manajemen Mutu ISO 9001:2015 di PPSDM Aparatur KESDM adalah agar terpeliharanya Sistem Manajemen Mutu ISO 9001:2015 di Pusat Pengembangan Sumber Daya Manusia Aparatur.&nbsp;</div>', '2023-07-06 07:58:53', '2023-07-06 07:58:53'),
	(23, 'Kegiatan Penyusunan Standar Operasional Prosedur (SOP) Pusat Pengembangan Sumber Daya Manusia Aparatur KESDM', 'Rapat SOP.jpg', '<div>Dengan adanya Peraturan Menteri ESDM No 15 tahun 2021 serta Peraturan Menteri RB Nomor 7 Tahun 2022, maka rangka menjalankan Tugas dan Fungsi Pusat Pengembangan Sumber Daya Manusia Aparatur KESDM perlu dilakukan penyesuaian, evaluasi dan pembaharuan terhadap Standar Operasional Prosedur di Pusat Pengembangan Sumber Daya Manusia Aparatur KESDM.<br><br></div><div>Maksud dari kegiatan ini untuk melaksanakan penyusunan standar operasional prosedur (SOP) sesuai dengan peraturan dan kebutuhan dalam melaksanakan operasional. Sedangkan, tujuan dari kegiatan ini adalah agar Pusat Pengembangan Sumber Daya Manusia Aparatur KESDM memiliki Dokumen Standar Operasional Prosedur (SOP) yang sesuai peraturan terbaru, sehingga pelaksanaan tugas dan fungsi Pusat Pengembangan Sumber Daya Manusia Aparatur KESDM dapat menjadi sesuai dan optimal. Sasaran/Keluaran yang dihasilkan pada kegiatan ini adalah Laporan Kegiatan Standar Operasional Prosedur (SOP) dan Standar Operasional Prosedur (SOP).<br><br></div><div>Standar Operasional Prosedur (SOP) yang disusun nantinya diharapkan dapat mendukung dan mendorong ke arah perbaikan penyelenggaraan Tugas dan Fungsi Pusat Pengembangan Sumber Daya Manusia Aparatur KESDM, sehingga dimasa yang akan datang bisa menjadi lebih baik dan berkinerja tinggi didukung dengan sumber daya manusia yang handal dan profesional.&nbsp;</div>', '2023-07-06 08:12:01', '2023-07-06 08:12:01');

